<template>
  <div class="container">
    <button type="button" class="button" @click="showModal">
      Create New Card!
    </button>
    <ModalCreateCart
        v-show="isModalVisible"
        @close="closeModal"
        @openWindow="openWindow" :data="this.cards"
        :editCard="editCard" :cardData="this.cardData" @handleImage="handleImage"
        @formSubmit="formSubmit"
    />
    <div style="display: flex; flex-wrap: wrap; min-height: 100vh;">
      <Card v-for="card in filteredData" :card="card" :itemId="card.id"
            :title="card.title" :description="card.description"
            :imageLink="card.imageLink" :date="card.date"
            @editCard="editCard" @deleteCard="deleteCard"/>
    </div>

  </div>
</template>

<script>
import ModalCreateCart from "../components/ModalCreateCart";
import Card from "../components/Card"
export default {
  data: () => ({
    isModalVisible:false,
    cardData: {
      cardTitle:'',
      cardName: '',
      cardDescription:'',
      cardDate: '',
      imageLink: '',
    },
    cards: [
      {
        id: 1,
        name: 'Mushex',
        title: 'Developer',
        description: 'Web',
        imageLink: 'data:image/jpeg;base64,',
      },
      {
        id: 2,
        name: 'Mushex',
        title: 'Developer',
        description: 'Web',
        imageLink: 'data:image/jpeg;base64,',
      },
      {
        id: 3,
        name: 'Mushex',
        title: 'Developer',
        description: 'Web',
        imageLink: 'data:image/jpeg;base64,',
      },
    ]
  }),
  components: {
    ModalCreateCart,
    Card,
  },
  computed:{
    filteredData(){
      console.log(Array.isArray(JSON.parse(localStorage.getItem('cards'))) )
      console.log( JSON.parse(localStorage.getItem('cards')))

      return JSON.parse(localStorage.getItem('cards'))
    },
    created() {
      if (localStorage.cards){
        this.cards = JSON.parse(localStorage.getItem('cards'))
      }else{
        localStorage.cards = JSON.stringify(this.cards)
      }
    },
  },
  watch: {
    cards: {
      handler(val) {
        localStorage.setItem('cards', JSON.stringify(this.cards))
      },
      deep:true
    }
  },
  methods: {
    showModal() {
      this.isModalVisible = !this.isModalVisible ;
    },
    closeModal() {
      this.showModal()
    },
    editCard(itemId) {
      const selectedItem = this.cards.filter(card => card.id === itemId)
      this.cardData.id = selectedItem[0].id
      this.cardData.cardTitle = selectedItem[0].title
      this.cardData.cardName = selectedItem[0].name
      this.cardData.cardDescription = selectedItem[0].description
      this.cardData.cardDate = selectedItem[0].date
      this.cardData.imageLink = selectedItem[0].imageLink
      this.showModal()
    },
    deleteCard(itemId) {
      this.cards = this.cards.filter(card => card.id !== itemId)
    },
    openWindow(){
      this.window = !this.window
      if (!this.window){
        this.cardData = {
          id: '',
          cardTitle:'',
          cardName: '',
          cardDescription:'',
          cardDate: '',
          imageLink: '',
        }
      }

    },
    formSubmit(){
      if(!this.cardData.id){
        this.cards.push({
          id: new Date().getTime(),
          title: this.cardData.cardTitle,
          name: this.cardData.cardName,
          description: this.cardData.cardDescription,
          date: this.cardData.cardDate,
          imageLink: this.cardData.imageLink,
        })
        this.openWindow()
      }else{
        this.cards.forEach(card => {
          if(card.id === this.cardData.id){
            card.title=this.cardData.cardTitle
            card.name=this.cardData.cardName
            card.description=this.cardData.cardDescription
            card.imageLink=this.cardData.imageLink
            card.date=this.cardData.cardDate
          }
        })
        this.openWindow()
      }
      this.showModal()
    },
    handleImage(e){
      const selectedImage = e.target.files[0]
      this.createBase64Image(selectedImage)
    },
    createBase64Image(fileObject) {
      const reader = new FileReader();
      reader.onloadend = () => {
        this.cardData.imageLink = reader.result
      }
      reader.readAsDataURL(fileObject);
    },
  }
}
</script>

